
public class Greatest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a=50;
       int b=20;
       int c=70;
       if(a>b&&a>c)
       {
    	   System.out.println("a is greatest="+a);
       }
       else if(b>c&&b>a)
       {
    	   System.out.println("b is greatest="+b);
       }
       else
       {
    	   System.out.println("c is greatest="+c);
       }
	}

}
